import React from "react";
import "./SearchBar.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { library } from "@fortawesome/fontawesome-svg-core";

library.add(faTimes);
library.add(faSearch);


export default class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = { inputValue: "", buttonDisabled: true };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClear = this.handleClear.bind(this);
  }

  handleChange(event) {
    this.setState({ inputValue: event.target.value });

    if (event.target.value === "") {
      this.setState({ buttonDisabled: true });
    } else {
      this.setState({ buttonDisabled: false });
    }
  }

  handleSubmit(event) {
    event.preventDefault();
    alert("A inputValue was submitted: " + this.state.inputValue);




    event.preventDefault();
  }

  askSpotify(url = ``, data = {}) {
    // Default options are marked with *
      return fetch(url, {
          method: "GET", // *GET, POST, PUT, DELETE, etc.
          mode: "cors", // no-cors, cors, *same-origin
          cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
          credentials: "same-origin", // include, *same-origin, omit
          headers: {
              "Content-Type": "application/json; charset=utf-8",
              // "Content-Type": "application/x-www-form-urlencoded",
          },
          redirect: "follow", // manual, *follow, error
          referrer: "no-referrer", // no-referrer, *client
          body: JSON.stringify(data), // body data type must match "Content-Type" header
      })
      .then(response => response.json()); // parses response to JSON
  }

  handleClear(event) {
    this.setState({ inputValue: "", buttonDisabled: true });
  }

  render() {
    return (
      <div>
        <form noValidate onSubmit={this.handleSubmit}>
          <div className="form-row align-items-center">
            <div className="col-12">
              <label className="control-label">
                Search for your favorite music:
              </label>
            </div>
            <div className="input-group col-10 mb-2">
              <input
                type="text"
                className="form-control"
                id="search-term"
                name="search-term"
                placeholder="Search artist, song, album ..."
                value={this.state.inputValue}
                onChange={this.handleChange}
              />
              <span className={`input-group-text ${this.state.buttonDisabled ? "hidden" : ""}`} onClick={this.handleClear}>
                <FontAwesomeIcon icon="times" className="fa-sm"  />
              </span>
            </div>
            <div className="col-2">
              <button
                type="submit"
                className="btn btn-search mb-2 btn-block"
                disabled={this.state.buttonDisabled}

              >
                Search <FontAwesomeIcon icon="search" className="fa-sm"  />
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
