import React from "react";
import "./Tracklist.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSpotify } from "@fortawesome/free-brands-svg-icons";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import { library } from "@fortawesome/fontawesome-svg-core";

library.add(faTimes, faSpotify);

const tracks = [
  {
    id: "23344556",
    name: "Bohemian Rhapsody",
    album: {
      name: "A night at the opera",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/16f066184e92b296f9a202a326633a934607cb88"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  },
  {
    id: "990038833",
    name: "Don't stop me now",
    album: {
      name: "Jazz",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/9d4932142928310f8e4d00d20e26192100edb216"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  },
  {
    id: "7768888",
    name: "Another one bites the dust",
    album: {
      name: "The game",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/26b0d4f8f5b866bb81f74ad3091a878d9c1c28b0"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  }
];

export default class Tracklist extends React.Component {
  constructor(props) {
    super(props);
    this.state = { tracks: tracks };
  }

  render() {
    const listTracks = tracks.map(track => (
      <li className="list-group-item">
        <div className="row align-items-center">
          <div className="col-1">*</div>
          <div className="col">{track.name}</div>
          <div className="col">{track.album.name}</div>
          <div className="col">
            {track.artists[0].name}
            <br />
          </div>
          <div className="col">
            <img alt="album" src={`${track.album.images[2].url}`} />
          </div>
        </div>
      </li>
    ));

    return (
      <div>
        <div className="total-tracks">{tracks.length} results</div>
        <ul className="list-group">
          <li className="list-group-item list-title">
            <div className="row align-items-center">
              <div className="col-1" />
              <div className="col">Song</div>
              <div className="col">Album</div>
              <div className="col">Artists</div>
              <div className="col" />
            </div>
          </li>
          {listTracks}
        </ul>
      </div>
    );
  }
}
