import React, { Component, Fragment } from "react";
import Tracklist from "../tracklist/Tracklist";
import SearchBar from "../search-bar/SearchBar";

const tracks = [
  {
    id: "23344556",
    name: "Bohemian Rhapsody",
    album: {
      name: "A night at the opera",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/16f066184e92b296f9a202a326633a934607cb88"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  },
  {
    id: "990038833",
    name: "Don't stop me now",
    album: {
      name: "Jazz",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/9d4932142928310f8e4d00d20e26192100edb216"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  },
  {
    id: "7768888",
    name: "Another one bites the dust",
    album: {
      name: "The game",
      images: [
        { url: "" },
        { url: "" },
        {
          url:
            "https://i.scdn.co/image/26b0d4f8f5b866bb81f74ad3091a878d9c1c28b0"
        }
      ]
    },
    artists: [
      {
        id: "1434324324",
        name: "Queen"
      }
    ]
  }
];

const handleSubmit = inputValue => {
  fetch(
    `https://api.spotify.com/v1/search?q=${inputValue}&type=track&limit=10`,
    {
      crossDoman: true,
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        Authorization:
          "Bearer BQBniO_gGO8uLmIdID618ItbLGSLTVfFBW3YvyW1O3L3Z0VuNX6HBtlho0UggQ_d3egS6LyWltVOMqoO05du9tDgKd3EJ7xKUPWKfnQjrd-r_b8mCOVn4OkOATR4TnoKg9Mgt9O9o94c"
      }
    }
  )
    .then(response => response.json())
    .then(function(myJson) {
      console.log(JSON.stringify(myJson));
    });
};

class Player extends React.Component {
  render() {
    return (
      <React.Fragment>
        <SearchBar onSearch={this.handleSubmit} />
        <Tracklist />
      </React.Fragment>
    );
  }
}

export default Player;
