import React from "react";
import "./CurrentDateTime.css";

export default class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = { input: '' };
  }

  render() {
    return (
      <form novalidate="">
    <div className="form-row align-items-center">
        <div className="col-12">
            <label cclassNamelass="control-label">Search for your favorite music:</label>
            </div>
        <div className="input-group col-10 mb-2">
            <input type="text" className="form-control" id="search-term" 
            name="search-term" placeholder="Search artist, song, album ..." value="" />
            <span className="input-group-text clear">*</span>
        </div>
        <div className="col-2">
            <button type="submit" class="btn btn-search mb-2 btn-block" disabled="">Search **</button>
        </div>
    </div>
</form>
    );
  }
}
