import React from "react";
import "./SearchBar.css";

export default class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = { inputValue: "", buttonDisabled: true };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ inputValue: event.target.inputValue });
    if (this.state.inputValue == "") {
      this.setState({ buttonDisabled: true });
    }
  }

  handleSubmit(event) {
    alert("A inputValu was submitted: " + this.state.inputValu);
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row align-items-center">
            <div className="col-12">
              <label className="control-label">
                Search for your favorite music:
              </label>
            </div>
            <div className="input-group col-10 mb-2">
              <input
                type="text"
                className="form-control"
                id="search-term"
                name="search-term"
                placeholder="Search artist, song, album ..."
                inputValue={this.state.inputValue}
                onChange={this.handleChange}
              />
              <span className="input-group-text clear">*</span>
            </div>
            <div className="col-2">
              <button
                type="submit"
                className="btn btn-search mb-2 btn-block"
                disabled={this.state.buttonDisabled}
              >
                Search **
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
