import React, { Component, Fragment } from "react";
import tracklist from "../tracklist/tracklist";
import SearchBar from "../search-bar/SearchBar";
import Tracklist from "../tracklist/tracklist";

const handleSubmit = inputValue => {
  fetch(
    `https://api.spotify.com/v1/search?q=${inputValue}&type=track&limit=10`,
    {
      crossDoman: true,
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        Authorization:
          "Bearer BQBniO_gGO8uLmIdID618ItbLGSLTVfFBW3YvyW1O3L3Z0VuNX6HBtlho0UggQ_d3egS6LyWltVOMqoO05du9tDgKd3EJ7xKUPWKfnQjrd-r_b8mCOVn4OkOATR4TnoKg9Mgt9O9o94c"
      }
    }
  )
    .then(response => response.json())
    .then(function(myJson) {
      console.log(JSON.stringify(myJson));
      this.setState({ tracklist: myJson });
    });
};

class Player extends React.Component {
  constructor(props) {
    super(props);
    this.state = { tracklist: "" };
  }

  render() {
    return (
      <React.Fragment>
        <SearchBar onSearch={this.handleSubmit} />
        <Tracklist tracklist={this.state.tracklist} />
      </React.Fragment>
    );
  }
}

export default Player;
